import React, { useState, useEffect } from "react";
import styled from "styled-components";

function App() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const id = setInterval(() => {
      setTime(new Date());
    }, 1000);
    return () => clearInterval(id);
  }, []);

  return (
    <PageWrapper>
      <PageBlock>
        <Title>수능 타이머</Title>
        {time.toLocaleDateString()}
        {time.toLocaleTimeString()}
        <Dday>D-64</Dday>
        <ComunityBlock></ComunityBlock>
      </PageBlock>
    </PageWrapper>
  );
}

const PageWrapper = styled.div`
  width: 100%;
  height: 100vh;
  color: #fff;
  background-color: #000;
  display: flex;
  align-items: center;
`;
const PageBlock = styled.div`
  width: 100%;
  max-width: 1080px;
  background-color: #212121;
  height: 80vh;
  border-radius: 24px;
  margin: 0 auto;
  overflow: none;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Title = styled.div`
  font-size: 52px;
  font-weight: 700;
  margin-bottom: 20px;
`;

const Dday = styled.div`
  font-size: 52px;
  font-weight: 700;
  margin-bottom: 20px;
  color: red;
`;

const ComunityBlock = styled.div`
  width: 90%;
  height: 400px;
  border: 1px solid #000;
  border-radius: 5px;
`;

export default App;
