import React, { useState, useEffect } from "react";
import styled from "styled-components";
import "./App.css";

function App() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const id = setInterval(() => {
      setTime(new Date());
    }, 1000);
    return () => clearInterval(id);
  }, []);

  return (
    <PageWrapper>
      <PageBlock>
        <Title>수능 타이머</Title>
        {time.toLocaleDateString()}
        {time.toLocaleTimeString()}
        <Dday>D-64</Dday>
        <ComunityBlockWrapper>
          <ComunityBlock></ComunityBlock>
          <Designed>designed by hoodboy</Designed>
        </ComunityBlockWrapper>
      </PageBlock>
    </PageWrapper>
  );
}

const PageWrapper = styled.div`
  width: 100%;
  height: 100vh;
  color: #fff;
  background-color: #000;
  display: flex;
  align-items: center;

  @font-face {
    font-family: "Pretendard Variable";
    src: url("https://cdn.jsdelivr.net/gh/orioncactus/pretendard/dist/web/variable/pretendardvariable.css");
    font-weight: bold;
    font-style: normal;
  }
  font-family: -apple-system, BlinkMacSystemFont, "Apple SD Gothic Neo",
    Pretendard, Roboto, "Noto Sans KR", "Segoe UI", "Malgun Gothic",
    "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;
`;
const PageBlock = styled.div`
  width: 100%;
  max-width: 880px;
  padding: 50px;
  background-color: #212121;
  height: calc(90vh - 100px);

  align-items: center;
  border-radius: 24px;
  margin: 0 auto;
  overflow: none;
  display: flex;
  flex-direction: column;
`;

const Title = styled.div`
  font-size: 52px;
  font-weight: 700;
  margin-bottom: 20px;
`;

const Dday = styled.div`
  font-size: 52px;
  font-weight: 700;
  margin-bottom: 20px;
  color: red;
  margin: 50px 0;
`;

const ComunityBlockWrapper = styled.div`
  width: 100%;
  margin: 100px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const ComunityBlock = styled.div`
  width: 100%;
  height: 400px;
  background-color: #303030;
  border-radius: 12px;
`;

const Designed = styled.div`
  margin: 24px 0;
`;

export default App;
